mapper.select:

select * from m_member
	where m_email=#{m_email}
	and m_password=#{m_password}
	and m_status='active'

SELECT m_idx, m_email, m_password, m_nickname, m_status, m_registration_type
FROM m_member
WHERE m_email = #{googleId}

mapper.update:
update m_member

    m_password = #{m_password},
    m_nickname = #{m_nickname}

where m_idx = #{m_idx}
